<?php  define ("DB_HOST", "localhost"); // set database host 
define ("DB_USER", "root"); // set database user 
define ("DB_PASS","root"); // set database password 
define ("DB_NAME","quiz_master"); // set database name 
$link = mysql_connect(DB_HOST, DB_USER, DB_PASS) or die("Couldn't make connection."); $db = mysql_select_db(DB_NAME, $link) or die("Couldn't select database"); $databasetable = "YOUR_TABLE";  set_include_path(get_include_path() . PATH_SEPARATOR . 'Classes/'); include 'PHPExcel/IOFactory.php';  $inputFileName = 'discussdesk.xlsx'; try { $objPHPExcel = PHPExcel_IOFactory::load($inputFileName); } catch(Exception $e) { die('Error loading file "'.pathinfo($inputFileName,PATHINFO_BASENAME).'": '.$e->getMessage()); } $allDataInSheet = $objPHPExcel->getActiveSheet()->toArray(null,true,true,true); $arrayCount = count($allDataInSheet);
for($i=2;$i<=$arrayCount;$i++){ $userName = trim($allDataInSheet[$i]["A"]); $userMobile = trim($allDataInSheet[$i]["B"]); $query = "SELECT name FROM YOUR_TABLE WHERE name = '".$userName."' and email = '".$userMobile."'"; $sql = mysql_query($query); $recResult = mysql_fetch_array($sql); $existName = $recResult["name"]; if($existName=="") { $insertTable= mysql_query("insert into YOUR_TABLE (name, email) values('".$userName."', '".$userMobile."');"); $msg = 'Record has been added. <div style="Padding:20px 0 0 0;"><a href="http://www.discussdesk.com/import-excel-file-data-in-mysql-database-using-PHP.htm" target="_blank">Go Back to tutorial</a></div>'; } else { $msg = 'Record already exist. <div style="Padding:20px 0 0 0;"><a href="http://www.discussdesk.com/import-excel-file-data-in-mysql-database-using-PHP.htm" target="_blank">Go Back to tutorial</a></div>'; } } echo "<div style='font: bold 18px arial,verdana;padding: 45px 0 0 500px;'>".$msg."</div>"; ?>